import React, { useState } from "react";
import { Button, Typography, TextField } from "@material-ui/core";
import { useDispatch } from "react-redux";

import useStyles from "./styles";

const CommentSection = ({ post }) => {
    const classes = useStyles()
    const [comments, setComments] = useState([1, 2, 3, 4])
    const [comment, setComment] = useState('')
  console.log('post', post)
  return (
    <div>
        <div className={classes.commentsOuterContainer}>
            <div className={classes.commentInnerContainer}>
                <Typography gutterBottom variant="h6">评论</Typography>
                {comments.map((c, i) => (
                    <Typography key={i} gutterBottom variant="subtitle1">
                        评论 {i}
                    </Typography>
                )) }
            </div>
            <div style={{ width: '70%' }}>
                <Typography gutterBottom variant="h6">评论: </Typography>
                <TextField fullWidth maxRows={4} minRows={4} variant='outlined' label='评论' multiline value={comment} onChange={e => setComment(e.target.value)}/>
                <Button style={{ marginTop: '10px'}} fullWidth disabled={!comment} variant='contained'/>
            </div>
        </div>
    </div>
  )
};

export default CommentSection;
