import { CREATE, UPDATE, FETCH_ALL, DELETE, LIKE } from '../constants/actionTypes'

export default (state = [], action) => {
    switch (action.type) {
        case 'FETCH_ALL':
            return action.playload
        case 'CREATE':
            return [...state, action.playload]
        case 'UPDATE':
        case 'LIKE':
            return state.map(post => post._id === action.playload._id ? action.playload : post)
        case 'DELETE':
            return state.filter(post => post._id !== action.playload)
        default:
            return state
    }
}
