import { CREATE, UPDATE, FETCH_ALL, DELETE, FETCH_BY_SEARCH, START_LOADING, END_LOADING } from '../constants/actionTypes'

export default (state = {}, action) => {
    switch (action.type) {
        case START_LOADING: 
            return { ...state, isLoading: true }
        case END_LOADING:
            return { ...state, isLoading: false }
        case FETCH_ALL:
            return { 
                ...state, 
                posts: action.playload.data, 
                currentPage: action?.playload?.currentPage,
                numberOfPages: action?.playload?.numberOfPages 
            }
        case FETCH_BY_SEARCH:
            return {
                ...state,
                posts: action.playload
            }
        case CREATE:
            return [...state, action.playload]
        case UPDATE:
            return state.map(post => post._id === action.playload._id ? action.playload : post)
        case DELETE:
            return state.filter(post => post._id !== action.playload)
        default:
            return state
    }
}
