import React from 'react'
import { Card, CardActions, CardContent, CardMedia, Button, Typography } from '@material-ui/core'
import { ThumbUpAlt, Delete, MoreHoriz, ThumbUpAltOutlined } from '@material-ui/icons'
import moment from 'moment'
import { useDispatch } from 'react-redux'

import useStyles from './styles'
import { deletePost, likePost } from '../../../actions/posts'

const Post = ({ post, setCurrentId }) => {
  const classes = useStyles()
  const dispatch = useDispatch()
  const user = JSON.parse(sessionStorage.getItem('profile'))

  const Likes = () => {
    if (post.likes.length > 0) {
      return post.likes.find(like => like === (user?.result?.sub || user?.result?._id))
        ? (
          <>
            <ThumbUpAlt fontSize="small" />
            &nbsp;{post.likes.length > 2 ? `您和其他${post.likes.length - 1}人` : `${post.likes.length}人喜欢`}
          </>
        ) : (
          <>
            <ThumbUpAltOutlined fontSize="small" />
            &nbsp;{post.likes.length}人喜欢</>
        );
    }
    return <><ThumbUpAltOutlined fontSize="small" />&nbsp;喜欢</>;
  };

  return (
    <Card className={classes.card}>
      <CardMedia className={classes.media} image={post.selectedFile} title={post.title} />
      <div className={classes.overlay}>
        <Typography variant='h6'>{post.name}</Typography>
        <Typography variant='body2'>{moment(post.createdAt).fromNow()}</Typography>
      </div>
      <div className={classes.overlay2}>
        <Button style={{ color: 'white' }} size='small' 
          onClick={() => setCurrentId(post._id)}>
          <MoreHoriz fontSize='medium' />
        </Button>
      </div>
      <div className={classes.details}>
        <Typography variant='body2' color='textSecondary'>{post.tags.map(tag => `#${tag} `)}</Typography>
      </div>
      <Typography className={classes.title} variant='h5' gutterBottom>{post.title}</Typography>
      <CardContent>
        <Typography variant='body2' color='textSecondary' gutterBottom>{post.message}</Typography>
      </CardContent>
      <CardActions className={classes.cardActions}>
        <Button size='small' color='primary' 
          disabled={!user?.result}
          onClick={() => dispatch(likePost(post._id))}>
          {/* <ThumbUpAlt fontSize='small'/>
          喜欢 &nbsp;
          {post.likeCount} */}
          <Likes />
        </Button>
        {(user?.result?.sub === post?.creator || user?.result) }
      </CardActions>
    </Card>
  )
}

export default Post
