import React, { useState } from 'react'
import { Avatar, Button, Typography, Paper, Grid, Container, TextField } from '@material-ui/core'
import { LockOutlined } from '@material-ui/icons'
import { GoogleLogin, GoogleOAuthProvider } from '@react-oauth/google' // 实现谷歌第三方的登录
// import { FcGoogle } from "react-icons/fc"; //引入谷歌图标

import useStyles from './styles'
import Input from './Input'

const Auth = () => {
  const classes = useStyles()
  const [showPassword, setShowPassword] = useState(false)
  const [isSignUp, setIsSignUp] = useState(false)

  const handleSubmit = () => { }

  const handleChange = () => { }

  const handleShowPassword = () => setShowPassword(preShowPassword => !preShowPassword)

  const switchMode = () => {
    setIsSignUp(prevIsSignUp => !prevIsSignUp)
    handleShowPassword(false)
  }
  /* client-id: 511187713250-lau05uhtqlrq2hqfimct4m58umvrdmdr.apps.googleusercontent.com */

  /* client-password: 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 */

  return (
    <Container component='main' maxWidth='xs'>
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlined />
        </Avatar>
        <Typography variant='h5'>{isSignUp ? '注册' : '登录'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {
              isSignUp && (
                <>
                  <Input name='userName' label='用户名' onChange={handleChange} autoFocus />
                </>
              )
            }
            <Input name='email' label='邮箱' onChange={handleChange} type='email' />
            <Input name='password' label='密码' onChange={handleChange} type={showPassword ? 'text' : 'password'} handleShowPassword={handleShowPassword} />
            {isSignUp && <Input name='confirmPassword' label='再次输入密码' handleChange={handleChange}/>}
          </Grid>
          <Button type='submit' fullWidth variant='contained' color='primary' className={classes.submit}>
            {isSignUp ? '注册' : '登录'}
          </Button>
            <GoogleOAuthProvider clientId={`${process.env.REACT_APP_GOOGLE_API_TOKEN}`}>
              <GoogleLogin
                render={renderProps => (
                  <Button 
                    className={classes.googleButton}
                    type='button' 
                    onClick={renderProps.onClick} 
                    disabled={renderProps.disabled}>
                      <FcGoogle />谷歌登录
                  </Button>
                )}
                onSuccess={credentialResponse => successLogin(credentialResponse)}
                onError={() => console.log("登录失败！")}
                cookiePolicy="single_host_origin"
                size="large"
                shape="circle"
                text="signin_with"
                rectangular="circle"
                locale="zh_CN"
              ></GoogleLogin>
            </GoogleOAuthProvider>
          <Grid container justifyContent='flex-end'>
            <Grid item>
              <Button onClick={switchMode}>
                {isSignUp ? '已有账号? 登录' : '还没有账号? 马上注册'}
              </Button>
            </Grid>
          </Grid>
        </form>
      </Paper>
    </Container>
  )
}

export default Auth
