import { CREATE, UPDATE, FETCH_ALL, DELETE, FETCH_BY_SEARCH } from '../constants/actionTypes'

export default (state = [], action) => {
    switch (action.type) {
        case FETCH_ALL:
            return { ...state, posts: action.playload.data }
        case FETCH_BY_SEARCH:
            return action.playload
        case CREATE:
            return [...state, action.playload]
        case UPDATE:
            return state.map(post => post._id === action.playload._id ? action.playload : post)
        case DELETE:
            return state.filter(post => post._id !== action.playload)
        default:
            return state
    }
}
