import React, { useState } from 'react'
import { Avatar, Button, Typography, Paper, Grid, Container, TextField } from '@material-ui/core'
import { LockOutlined } from '@material-ui/icons'

import useStyles from './styles'
import Input from './Input'

const Auth = () => {
  const classes = useStyles()
  const isSignUp = false
  const [showPassword, setShowPassword] = useState(false)

  const handleSubmit = () => { }

  const handleChange = () => { }
  return (
    <Container component='main' maxWidth='xs'>
      <Paper className={classes.paper} elevation={3}>
        <Avatar className={classes.avatar}>
          <LockOutlined />
        </Avatar>
        <Typography variant='h5'>{isSignUp ? '注册' : '登录'}</Typography>
        <form className={classes.form} onSubmit={handleSubmit}>
          <Grid container spacing={2}>
            {
              isSignUp && (
                <>
                  <Input name='userName' label='username' onChange={handleChange} half autoFocus />
                </>
              )
            }
            <Input name='email' label='email address' onChange={handleChange} type='email'/>
            <Input name='password' label='password' onChange={handleChange} type={show}/>
          </Grid>
        </form>
      </Paper>
    </Container>
  )
}

export default Auth
